mapper.select:
select *  from borrow where created_at is not null

      and student_id = #{page.key.studentId}


      and teacher_id = #{page.key.teacherId}


      and  room_no = #{page.key.roomNo}


      and checked = #{page.key.checked}



      and  DATE_FORMAT(class_time,'%Y-%m-%d') like '%' #{page.dateTime} '%'


order by
id desc


limit  #{page.startNum} , #{page.pageSize}

select count(id) as count from borrow  where created_at is not null

    and student_id = #{page.key.studentId}


    and teacher_id = #{page.key.teacherId}


    and checked = #{page.key.checked}


    and  room_no = #{page.key.roomNo}



    and  DATE_FORMAT(class_time,'%Y-%m-%d') like '%' #{page.dateTime} '%'
